import React from 'react';
import './App.css';
import FilterContainer from './components/FilterContainer.js';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    filter: 'all',
    filteredTasks: [],
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  filterTasks = (event) => {

    let updatedTasks = []

    if (event.target.name === 'All') {
      let updatedTasks = this.state.tasks
    } else {
      let updatedTasks = this.state.tasks.filter (task => {
        return task.category === event.target.name
      })

    }
    this.setState ({
      filteredTasks: updatedTasks
    })
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <FilterContainer className="categories" categories={CATEGORIES} tasks={this.state.tasks} filterTasks={this.filterTasks}/>
      </div>
    );
  }
}

export default App;
