import React from 'react';
import './App.css';
import FilterContainer from './components/FilterContainer.js';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    currentFilter: 'all',
    filteredTasks: [],
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  componentDidMount() {
    this.setState ({
      filteredTasks: this.state.tasks
    })
  }

  filterTasks = (event) => {
    let currentFilter = event.target.name
    if (event.target.name !== 'All') {
      let updatedTasks = this.state.tasks.filter ((task) => {
        return task.category === event.target.name
      })
      this.setState ({
        filteredTasks: updatedTasks
      })
    } else {
      this.setState ({
        currentFilter,
        filteredTasks: this.state.tasks
      })
    }
  }

  newTask = (event) => {
    event.preventDefault()

    debugger

    this.setState ({
      tasks: [
        ...this.state.tasks,
        {
          text: event.target.input.value,
          code: event.target.code.value
        }
      ]
    })

    if (event.target.code.value === this.state.currentFilter) {
      this.setState ({
        filteredTasks: [
          ...this.state.filteredTasks,
          {
            text: event.target.input.value,
            code: event.target.code.value,
          }
        ]
      })
    }
    // debugger
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <FilterContainer className="categories" categories={CATEGORIES} filteredTasks={this.state.filteredTasks} filterTasks={this.filterTasks} newTask={this.newTask} />
      </div>
    );
  }
}

export default App;
